
UPDATE [dbo].[AccountTypes] SET [IsAssignable] = 1 
WHERE [Id] IN (SELECT [AccountTypeId] FROM @Accounts);

-- INSERT INTO @ValidationErrors
EXEC [api].[Accounts__Save]
	@Entities = @Accounts,
	@ReturnIds = 0,
	@UserId = @AdminUserId;
	
IF EXISTS (SELECT [Key] FROM @ValidationErrors)
BEGIN
	Print 'Accounts: Error Provisioning'
	SET @ValidationErrorsJson = CAST ((SELECT * FROM @ValidationErrors FOR JSON AUTO) AS NVARCHAR (MAX));
	PRINT (@ValidationErrorsJson)
	GOTO Err_Label;
END;

DELETE FROM @IndexedIds;
WHERE AccountTypeId IN (SELECT [Id] FROM dbo.AccountTypes WHERE [IsActive] = 0)
-- Detailed rental properties reveivable, staff debtors, sundry debtors
OR CODE IN (N'12020701' , N'12050101', N'12050201')
-- Detailed Cash on hand
OR CODE BETWEEN N'12070111' AND N'12070113'
-- Detaild bank account
OR CODE BETWEEN N'12070131' AND N'12070133'
--OR CODE BETWEEN N'12070131' AND N'12070133'

EXEC [api].[Accounts__Activate]
	@Ids = @IndexedIds,
	@IsActive = 0,
	@UserId = @AdminUserId;
	
IF EXISTS (SELECT [Key] FROM @ValidationErrors)
BEGIN
	Print 'Accounts: Error Deactivating'
	SET @ValidationErrorsJson = CAST ((SELECT * FROM @ValidationErrors FOR JSON AUTO) AS NVARCHAR (MAX));
	PRINT (@ValidationErrorsJson)

	GOTO Err_Label;
END;