DECLARE @Translations TABLE
(
	[Id]					INT PRIMARY KEY IDENTITY,
	[TableName]				NVARCHAR(50),
	[SourceEnglishWord]		NVARCHAR (100),
	[DestinationCultureId]	NVARCHAR (5),
	[Form]					NCHAR (1)			CHECK([Form] IN (N's', N'p', N'n')),
	--UNIQUE([TableName], [SourceEnglishWord], [DestinationCultureId], [Form]),
	[DestinationWord]		NVARCHAR (100)		NOT NULL
);

WITH NonDuplicateTranslationIds([Id]) AS
(
	SELECT MIN([Id])
	FROM @Translations
	GROUP BY [TableName], [SourceEnglishWord], [DestinationCultureId], [Form]
)
SELECT [TableName],[SourceEnglishWord],[DestinationCultureId],[DestinationWord],[Form]
FROM @Translations
WHERE [Id] IN (SELECT [Id] FROM NonDuplicateTranslationIds)
